mapper.select:
SELECT id AS id, company_name AS companyName FROM bus_company WHERE is_deleted = 0
AND id IN
#{id}

SELECT c.id AS id, c.company_name AS companyName, c.company_type AS companyType, c.address AS address FROM bus_company c WHERE c.is_deleted = 0
AND ( c.company_name LIKE CONCAT('%', #{keyword}, '%') OR c.address LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY c.update_time DESC, c.id DESC LIMIT #{limit}

SELECT c.id AS id, c.company_name AS companyName, c.province AS province, c.city AS city, c.license_img_url AS logo, COUNT(s.id) AS count, GROUP_CONCAT(DISTINCT s.category_name) AS categoryNamesStr FROM bus_company c LEFT JOIN bus_supply s ON c.id = s.company_id AND s.is_deleted = 0 WHERE c.is_deleted = 0
AND c.province LIKE CONCAT('%', #{region}, '%')
GROUP BY c.id ORDER BY count DESC, c.id DESC LIMIT #{limit}

SELECT c.id AS id, c.company_name AS companyName, c.province AS province, c.city AS city, c.license_img_url AS logo, COUNT(r.id) AS count, GROUP_CONCAT(DISTINCT r.category_name) AS categoryNamesStr FROM bus_company c LEFT JOIN bus_requirement r ON c.id = r.company_id AND r.is_deleted = 0 WHERE c.is_deleted = 0
AND r.category_name = #{categoryName}
GROUP BY c.id ORDER BY count DESC, c.id DESC LIMIT #{limit}
